import React, { useState } from "react";
import axios from "axios";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
// import { navigate } from "@reach/router";

const WorkoutForm = (props) => {
  const [type, setType] = useState("");
  const [date, setDate] = useState("");
  const [location, setLocation] = useState("");
  const [workoutLength, setWorkoutLength] = useState("");
  const [completed, setCompleted] = useState("");
  // const [errors, setErrors] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // setErrors([])

    // axios.[props.method](props.url, {
    //   type,
    //   date,
    //   location,
    //   workoutLength,
    //   completed
    // })
    //   .then(res => {
    //     console.log('Response: ', res.data)
    //     navigate('/workouts')
    //   })
    //   .catch(err => {
    //     const errorResponse = err.response.data.errors;
    //     const errorArr = [];
    //     for (const key of Object.keys(errorResponse)) {
    //       errorArr.push(errorResponse[key].message)
    //     }
    //     setErrors(errorArr);
    //     console.log(err.response.data)
    //   })
  };

  return (
    <div class="container">
      {/* Workout Type */}
      <div class="form-group">
        <label for="workoutType">Type of Workout</label>
        <input
          type="text"
          class="form-control"
          placeholder="What did you do today?"
          name="type"
          value={type}
          onChange={(e) => setType(e.target.value)}
        />
      </div>

      {/* Date */}
      <div class="form-group">
        <label>Date:</label>
        <DatePicker
          closeOnScroll={true}
          selected={date}
          onChange={(d) => setDate(d)}
        />
      </div>

      {/* Location */}
      <div class="form-group">
        <label for="location">Location</label>
        <input
          type="text"
          class="form-control"
          placeholder="Enter Location"
          name="location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
      </div>

      {/* workout length */}
      <div class="form-group">
        <label for="location">Workout Length</label>
        <input
          type="text"
          class="form-control"
          placeholder="Enter Workout Length"
          name="workoutLength"
          value={workoutLength}
          onChange={(e) => setWorkoutLength(e.target.value)}
        />
      </div>

      {/* Dropdown Menu - Select Input */}
      <div className="form-group">
        <label> Smashed it? </label>
        <select
          value={completed}
          className="form-control"
          onChange={(e) => setCompleted(e.target.value)}
        >
          <option value="default">Yes</option>
          <option value="No">No</option>
        </select>
      </div>

      {/* Submit Button */}
      <input
        type="submit"
        value="Add Workout!"
        name="submit"
        className="add-btn"
      />

      {/* {errors.map( (err, index) => <h3 className="errors" key={index}> {err}</h3>)} */}
    </div>
  );
};

export default WorkoutForm;
